window.onload = function () {
  if (page_data.user && page_data.user !== "Guest") {
    const urlParams = new URLSearchParams(window.location.search);
    const redirectTo = urlParams.get('redirect-to');

    window.location.href = redirectTo || '/profile';
  }
   const showPasswordCheckbox = document.getElementById("show_password");
  const passwordInput = document.getElementById("password");
  const confirmPasswordInput = document.getElementById("confirm_password");

  if (showPasswordCheckbox && passwordInput && confirmPasswordInput) {
    showPasswordCheckbox.setAttribute('type', 'checkbox');

    showPasswordCheckbox.addEventListener("change", function () {
      if (this.checked) {
        passwordInput.setAttribute('type', 'text');
        confirmPasswordInput.setAttribute('type', 'text');
      } else {
        passwordInput.setAttribute('type', 'password');
        confirmPasswordInput.setAttribute('type', 'password');
      }
    });
  }
};

document.getElementById('register_form').addEventListener('submit', async function(e) {
  e.preventDefault(); // Prevent default form submission

  const form = e.target;
  const formData = new FormData(form);
  const csrfToken = page_data.csrf_token;

  const password = formData.get('password');
  const confirmPassword = formData.get('confirm_password');

  // Check if passwords match
  if (password !== confirmPassword) {
    Toastify({
      text: "âŒ Passwords do not match.",
      close: true,
      gravity: "top",
      position: "center",
      stopOnFocus: true,
      style: {
        background: "linear-gradient(to right, #ff5f6d, #ffc371)",
      }
    }).showToast();
    return;
  }

  // Convert form data to JSON
  const data = {};
  formData.forEach((value, key) => {
    data[key] = value;
  });

  try {
    const response = await fetch('/api/method/awesome_commerce.api.register.register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'x-frappe-csrf-token': csrfToken,
      },
      body: JSON.stringify({ doc: data })
    });

    const result = await response.json();

    if (response.ok && result.message) {
      Toastify({
        text: 'ðŸŽ‰ Registration successful!',
        close: true,
        gravity: "top",
        position: "center",
        stopOnFocus: true,
        style: {
          background: "linear-gradient(to right, #00b09b, #96c93d)",
        }
      }).showToast();
      window.location.href = '/profile'
    } else {
         if (result._server_messages) {
              const messages = JSON.parse(result._server_messages);
              const firstMessage = JSON.parse(messages[0]);
              const cleanMessage = firstMessage.message.replace(/<[^>]*>/g, ''); 
              errorMessage = `âŒ ${cleanMessage}`;

              Toastify({
                text: errorMessage,
                close: true,
                gravity: "top",
                position: "center",
                stopOnFocus: true,
                style: {
                  background: "linear-gradient(to right, #ff5f6d, #ffc371)",
                }
              }).showToast();
         }
    }
  } catch (error) {
    console.error('Error:', error);
    Toastify({
      text: 'âš ï¸ An error occurred. Please try again.',
      close: true,
      gravity: "top",
      position: "center",
      stopOnFocus: true,
      style: {
        background: "linear-gradient(to right, #f857a6, #ff5858)",
      }
    }).showToast();
  }
});

document.getElementById("login_form").addEventListener("submit", function(e) {
  e.preventDefault();

  const email = document.querySelector('#login_form input[type="text"]').value;
  const password = document.querySelector('#login_form input[type="password"]').value;

  fetch('/api/method/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      usr: email,
      pwd: password
    })
  })
  .then(response => response.json())
  .then(data => {
    if (data.full_name) {
      Toastify({
        text: `ðŸ‘‹ Welcome ${data.full_name}!`,
        close: true,
        gravity: "top",
        position: "center",
        stopOnFocus: true,
        style: {
          background: "linear-gradient(to right, #00b09b, #96c93d)",
        }
      }).showToast();

      setTimeout(() => {
        window.location.href = data.home_page || '/';
      }, 1200); // small delay to show toast
    } else {
      Toastify({
        text: "âŒ Invalid credentials. Please try again.",
        close: true,
        gravity: "top",
        position: "center",
        stopOnFocus: true,
        style: {
          background: "linear-gradient(to right, #ff5f6d, #ffc371)",
        }
      }).showToast();
    }
  })
  .catch(error => {
    console.error('Login error:', error);
    Toastify({
      text: "âš ï¸ An error occurred during login. Please try again later.",
      close: true,
      gravity: "top",
      position: "center",
      stopOnFocus: true,
      style: {
        background: "linear-gradient(to right, #f857a6, #ff5858)",
      }
    }).showToast();
  });
});


