document.addEventListener("DOMContentLoaded", function () {
    checkBillingDetails(false);
    deliverSame(true);
});

function checkBillingDetails(validate = true) {
    let firstName = document.querySelector('[name="first_name"]');
    let lastName = document.querySelector('[name="last_name"]');
    let email = document.querySelector('[name="email_id"]');
    let password = document.querySelector('[name="password"]');
    let confirmPassword = document.querySelector('[name="confirm_password"]');
    let telephone = document.querySelector('[name="phone"]');
    let addressLine1 = document.querySelector('[name="address_line1"]');
    let addressLine2 = document.querySelector('[name="address_line2"]');
    let town = document.querySelector('[name="city"]');
    let country = document.querySelector('[name="country"]');
    let postcode = document.querySelector('[name="pincode"]');
    let state = document.querySelector('[name="state"]');
    let isLoggedIn = document.querySelector('[name="user_logged_in"]');
    let attributeValue = isLoggedIn ? isLoggedIn.getAttribute('value') : null;

    resetRedBorders();

    if (attributeValue) {
        // User is logged in, make fields readonly
        let fieldsToDisable = [
            firstName, lastName, email, password, confirmPassword,
            telephone, addressLine1, addressLine2, town, country,
            postcode, state
        ];
        fieldsToDisable.forEach(field => {
            if (field) field.readOnly = true;
        });

        // Instead of validating address fields, check if billing_address radio is selected
        const selectedBilling = document.querySelector('[name="billing_address"]:checked');

        if (!selectedBilling) {
            if (validate) {
                Toastify({
                    text: "Please select a billing address.",
                    close: true,
                    gravity: "top",
                    position: "center",
                    stopOnFocus: true,
                    style: {
                        background: "linear-gradient(to right, #ff416c, #ff4b2b)",
                    }
                }).showToast();
            };
            isBillingDetailsCompleted = false;
            return;
        }

        isBillingDetailsCompleted = true;
        showTab(1);
        return;
    }

    // User is not logged in: validate all form fields as usual
    if (
        firstName.value && email.value &&
        (password.value && confirmPassword.value) &&
        telephone.value && town.value && postcode.value &&
        addressLine1.value
    ) {
        if (password.value !== confirmPassword.value) {
            password.style.border = "1px solid red";
            confirmPassword.style.border = "1px solid red";

            Toastify({
                text: "Passwords do not match",
                close: true,
                gravity: "top",
                position: "center",
                stopOnFocus: true,
                style: {
                    background: "linear-gradient(to right, #ff416c, #ff4b2b)",
                }
            }).showToast();
            isBillingDetailsCompleted = false;
            return;
        }
        isBillingDetailsCompleted = true;
        document.getElementById('tab-1').classList.remove('disabled');
        let deliverSameCheckbox = document.querySelector('[name="deliver_same"]');

        const preview = document.getElementById("address_preview");
        if (preview) {
            preview.querySelector(".address_line1").innerHTML = addressLine1.value || '';
            preview.querySelector(".city").innerHTML = town.value || '';
            preview.querySelector(".country").innerHTML = country.value || '';
            preview.querySelector(".pincode").innerHTML = postcode.value || '';
            preview.querySelector(".phone").innerHTML = telephone.value || '';
        }

        if (deliverSameCheckbox.checked) {
            deliverSame(true);
            const shippingPreview = document.getElementById("shipping_address_preview");
            if (shippingPreview) {
                shippingPreview.querySelector(".address_line1").innerHTML = addressLine1.value || '';
                shippingPreview.querySelector(".city").innerHTML = town.value || '';
                shippingPreview.querySelector(".country").innerHTML = country.value || '';
                shippingPreview.querySelector(".pincode").innerHTML = postcode.value || '';
                shippingPreview.querySelector(".phone").innerHTML = telephone.value || '';
            }
            showTab(2);
        } else {
            showTab(1);
        }

    } else {
        isBillingDetailsCompleted = false;
        if (validate) {
            applyRedBorders([
                firstName, lastName, email, password, confirmPassword,
                telephone, town, postcode, addressLine1
            ]);
        }
    }
}


function checkDeliveryDetails() {
    let deliverAddressLine1 = document.querySelector('[name="deliver_address_line_1"]');
    let deliverTown = document.querySelector('[name="deliver_town"]');
    let deliverPostcode = document.querySelector('[name="deliver_postcode"]');
    let deliverCountry = document.querySelector('[name="deliver_country"]');
    let isLoggedIn = document.querySelector('[name="user_logged_in"]');
    let attributeValue = isLoggedIn ? isLoggedIn.getAttribute('value') : null;
    let telephone = document.querySelector('[name="phone"]');

    resetRedBorders();

    if (attributeValue) {
        const selectedShipping = document.querySelector('[name="shipping_address"]:checked');

        if (!selectedShipping) {
            Toastify({
                text: "Please select a shipping address.",
                close: true,
                gravity: "top",
                position: "center",
                stopOnFocus: true,
                style: {
                    background: "linear-gradient(to right, #ff416c, #ff4b2b)",
                }
            }).showToast();
            isDeliveryDetailsCompleted = false;
            return;
        }

        isDeliveryDetailsCompleted = true;
        showTab(2);
        return;
    }
    else{
        const shippingPreview = document.getElementById("shipping_address_preview");
        if (shippingPreview) {
            shippingPreview.querySelector(".address_line1").innerHTML = deliverAddressLine1.value || '';
            shippingPreview.querySelector(".city").innerHTML = deliverTown.value || '';
            shippingPreview.querySelector(".country").innerHTML = deliverCountry.value || '';
            shippingPreview.querySelector(".pincode").innerHTML = deliverPostcode.value || '';
            shippingPreview.querySelector(".phone").innerHTML = telephone.value || '';
        }
    }

    if (deliverAddressLine1.value && deliverTown.value && deliverPostcode.value) {
        isDeliveryDetailsCompleted = true;
        document.getElementById('tab-2').classList.remove('disabled');
        showTab(2);
    } else {
        isDeliveryDetailsCompleted = false;
        applyRedBorders([deliverAddressLine1, deliverTown, deliverPostcode]);
    }
}



// Function to apply red borders to empty fields
function applyRedBorders(fields) {
    fields.forEach(field => {
        if (!field.value) {
            field.style.border = "1px solid red";
        }
    });
}


function resetRedBorders() {
    let allFields = document.querySelectorAll('input, textarea, select');
    allFields.forEach(field => {
        field.style.border = ""; // Reset any red border
    });
}

// Lock navigation to the next tab
function nextTab(tabIndex) {
    let deliverSameCheckbox = document.querySelector('[name="deliver_same"]');

    switch (tabIndex) {
        case 1:
            checkBillingDetails()
            if (isBillingDetailsCompleted) {
                if (deliverSameCheckbox.checked) {
                    showTab(2);
                } else {
                    showTab(1);
                }
            }
            break;
        case 2:
            checkDeliveryDetails();
            if (isDeliveryDetailsCompleted) {
                showTab(2);
            }
            break;
        default:
            showTab(tabIndex);
    }
}


function removeRedBorderOnInput() {
    this.style.border = ""; 
}

// Show the appropriate tab
function showTab(tabIndex) {
    let tabs = document.querySelectorAll('.tab');
    tabs.forEach(tab => tab.classList.remove('active'));
    document.getElementById(`tab-${tabIndex}`).classList.add('active');
    document.querySelectorAll('.tab-button').forEach((button, i) => {
        button.classList.toggle('active', i === tabIndex);
    });
}

document.querySelector('[name="deliver_same"]').addEventListener('change', function(event) {
    deliverSame(event.target.checked);
});

function deliverSame(checked){
    let isLoggedIn = document.querySelector('[name="user_logged_in"]');
    let attributeValue = isLoggedIn ? isLoggedIn.getAttribute('value') : null;
    if(!attributeValue){
        if (checked) {
            document.querySelector('[name="deliver_address_line_1"]').value = document.querySelector('[name="address_line1"]').value;
            document.querySelector('[name="deliver_address_line_2"]').value = document.querySelector('[name="address_line2"]').value;
            document.querySelector('[name="deliver_town"]').value = document.querySelector('[name="city"]').value;
            document.querySelector('[name="deliver_country"]').value = document.querySelector('[name="country"]').value;
            document.querySelector('[name="deliver_postcode"]').value = document.querySelector('[name="pincode"]').value;
            document.querySelector('[name="deliver_state"]').value = document.querySelector('[name="state"]').value;
        } else {
            // If the checkbox is unchecked, clear the Delivery Address fields
            document.querySelector('[name="deliver_address_line_1"]').value = '';
            document.querySelector('[name="deliver_address_line_2"]').value = '';
            document.querySelector('[name="deliver_town"]').value = '';
            document.querySelector('[name="deliver_country"]').value = '';
            document.querySelector('[name="deliver_postcode"]').value = '';
            document.querySelector('[name="deliver_state"]').value = '';
        }
    }
}

document.querySelectorAll('.tab-button').forEach((button, index) => {
    if (index > 0) {
        button.setAttribute('disabled', 'true');
    }
});


document.querySelectorAll('input, textarea, select').forEach(field => {
    field.addEventListener('input', removeRedBorderOnInput);
});

function prevTab(index) {
    showTab(index);
}


function updateAddressPreview(addressType, addressName) {
  fetch(`/api/resource/Address/${addressName}`)
    .then(res => res.json())
    .then(({ data }) => {
      if (!data) return;

      const preview = document.getElementById(
        addressType === "billing" ? "address_preview" : "shipping_address_preview"
      );

      if (preview) {
        preview.querySelector(".address_line1").innerHTML = data.address_line1 || '';
        preview.querySelector(".city").innerHTML = data.city || '';
        preview.querySelector(".country").innerHTML = data.country || '';
        preview.querySelector(".pincode").innerHTML = data.pincode || '';
        preview.querySelector(".phone").innerHTML = data.phone || '';
      }
    })
    .catch(err => {
      console.error("Error fetching address:", err);
    });
}

document.addEventListener("DOMContentLoaded", () => {
  document.querySelectorAll('.billing-radio').forEach(input => {
    input.addEventListener('change', () => {
      if (input.checked) {
          let deliverSameInput = document.querySelector('[name="deliver_same"]');

          // Check if the checkbox is checked on page load
          if (deliverSameInput && deliverSameInput.checked) {
              updateAddressPreview("billing", input.value);
              updateAddressPreview("shipping", input.value);
          }
        else{
                updateAddressPreview("billing", input.value);
            }
      }
    });

    // Trigger on first load if already selected
    if (input.checked) {
      updateAddressPreview("billing", input.value);
    }
  });

  document.querySelectorAll('.shipping-radio').forEach(input => {
    input.addEventListener('change', () => {
      if (input.checked) {
        updateAddressPreview("shipping", input.value);
      }
    });

    if (input.checked) {
      updateAddressPreview("shipping", input.value);
    }
  });
});



function getSelectedAddress(addressType) {
  let selectedAddress = document.querySelector(`input[name="${addressType}"]:checked`);
  return selectedAddress ? selectedAddress.value : null;
}

