function submitForm() {
    // event.preventDefault();

    // Collect form data
    const form = document.getElementById('profileForm');
    const formData = new FormData(form);

    // Build the JSON object from the form data
    const formObject = {};
    formData.forEach((value, key) => {
      formObject[key] = value;
    });

    // Create the data object to send with the request
    const data = {
      ...formObject,
      name: formObject['email'],
      doctype: "User",
      web_form_name: "edit-profile"
    };


    const payload = {
      data: JSON.stringify(data), 
      web_form: "edit-profile",
      for_payment: false,
      cmd: "frappe.website.doctype.web_form.web_form.accept"
    };


    const csrfToken = page_data.csrf_token;


    fetch('/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'x-frappe-cmd': 'frappe.website.doctype.web_form.web_form.accept',
        'x-frappe-csrf-token': csrfToken,
        'x-requested-with': 'XMLHttpRequest',
        'Accept': 'application/json, text/javascript, */*; q=0.01',
      },
      body: JSON.stringify(payload)
    })
    .then(response => response.json())
    .then(data => {
      Toastify({
          text: 'Profile updated successfully!',
          close: true,
          gravity: "top",
          position: "center",
          stopOnFocus: true,
          style: {
            background: "linear-gradient(to right, #00b09b, #96c93d)",
          }
        }).showToast();

        setTimeout(() => {
          window.location.href = '/profile?showTab=1';  
        }, 500);
    })
    .catch(error => {
      console.error('Error:', error); 
    });
}

function changePassword() {

    // Collect form data
    const form = document.getElementById('change_password');
    const formData = new FormData(form);

    // Build the JSON object from the form data
    const formObject = {};
    formData.forEach((value, key) => {
      formObject[key] = value;
    });

    const data = {
      ...formObject,
      logout_all_sessions: 1
    };

    const { new_password, confirm_password } = formObject;

      // Check if new_password and confirm_password match
      if (new_password !== confirm_password) {
        alert("New password and confirm password do not match.");
        return;
    } 

    const csrfToken = page_data.csrf_token;


    fetch('/api/method/frappe.core.doctype.user.user.update_password', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'x-frappe-csrf-token': csrfToken,
        'x-requested-with': 'XMLHttpRequest',
        'Accept': 'application/json, text/javascript, */*; q=0.01',
      },
      body: JSON.stringify(data)
    })
    .then(response => response.json())
    .then(data => {
      Toastify({
          text: 'Password changed successfully!',
          close: true,
          gravity: "top",
          position: "center",
          stopOnFocus: true,
          style: {
            background: "linear-gradient(to right, #00b09b, #96c93d)",
          }
        }).showToast();

        setTimeout(() => {
          window.location.href = '/profile?showTab=2'; 
        }, 500);
    })
    .catch(error => {
      console.error('Error:', error); 
    });
}

