    // Hide everything in tab-0
    tab0.innerHTML = '';

    // Move orderDetails to tab-0 and show it
    tab0.appendChild(orderDetails);
    orderDetails.style.display = 'block';
  });
});

document.addEventListener('click', (e) => {
  if (e.target.classList.contains('order_back_btn')) {
    const tab0 = document.querySelector('#tab-0');
    const orderDetails = tab0.querySelector('.order_details');

    // Hide orderDetails
    orderDetails.style.display = 'none';

    // Restore tab-0 content
    tab0.innerHTML = previousTabContent;

    // Re-inject the hidden orderDetails back to its original place
    if (originalParent) {
      originalParent.appendChild(orderDetails);
    }

    // Re-bind events since we replaced tab content
    rebindButtons();
  }
});

// Function to reattach events after DOM replacement
function rebindButtons() {
  document.querySelectorAll('.order_details_btn').forEach(button => {
    button.addEventListener('click', () => {
      const tab0 = document.querySelector('#tab-0');
      const orderSection = button.closest('.order_section');
      const orderDetails = orderSection.querySelector('.order_details');

      previousTabContent = tab0.innerHTML;
      originalParent = orderSection;

      tab0.innerHTML = '';
      tab0.appendChild(orderDetails);
      orderDetails.style.display = 'block';
    });
  });
}


