document.addEventListener('DOMContentLoaded', function () {
  const originalDrawer = document.getElementById('category_drawer');
  const drawerBtn = document.getElementById('category_drawer_btn');

  // Create and append overlay
  const overlay = document.createElement('div');
  overlay.id = 'drawer_overlay';
  document.body.appendChild(overlay);

  // Clone the drawer
  const drawer = originalDrawer.cloneNode(true);
  drawer.id = 'category_drawer_clone';
  drawer.style.display = 'block'; // ensure it's not display: none
  document.body.appendChild(drawer);

  // Style the cloned drawer
  Object.assign(drawer.style, {
    position: 'fixed',
    top: '0',
    left: '-300px', // initially hidden off-screen
    width: '300px',
    height: '100%',
    background: '#fff',
    boxShadow: '2px 0 10px rgba(0,0,0,0.1)',
    transition: 'left 0.3s ease',
    zIndex: '9999',
    overflowY: 'auto',
    display: 'block' // important to override inherited display: none
  });

  // Style overlay
  Object.assign(overlay.style, {
    position: 'fixed',
    top: '0',
    left: '0',
    right: '0',
    bottom: '0',
    background: 'rgba(0,0,0,0.4)',
    zIndex: '9998',
    display: 'none'
  });

  // Show drawer
  drawerBtn.addEventListener('click', () => {
    drawer.style.left = '0';
    overlay.style.display = 'block';
  });

  // Hide drawer
  overlay.addEventListener('click', () => {
    drawer.style.left = '-300px';
    overlay.style.display = 'none';
  });
